//
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import useGerenciadorProdutos from "./useProduto";

const TelaProdutoDetalhes = () => {
  // Pega o 'id' da URL (ex: /detalhes/123)
  const { id } = useParams();
  const navigate = useNavigate();

  const {
    listaProdutos,
    alterarProduto
  } = useGerenciadorProdutos();

  // Estados locais para os campos do formulário de edição
  const [nome, setNome] = useState("");
  const [valor, setValor] = useState("");

  // useEffect para carregar os dados do produto quando o componente montar
  useEffect(() => {
    const produtoParaEditar = listaProdutos.find(p => p.id.toString() === id);

    if (produtoParaEditar) {
      setNome(produtoParaEditar.nome);
      setValor(produtoParaEditar.valor);
    } else {
      // Se não encontrar o produto, volta para a página inicial
      alert("Produto não encontrado!");
      navigate("/");
    }
  }, [id, listaProdutos, navigate]);

  const handleSalvar = () => {
    if (!nome || !valor) {
      alert("Nome e valor são obrigatórios.");
      return;
    }

    const confirmou = window.confirm("Deseja salvar as alterações neste produto?");
    if (confirmou) {
      const produtoEditado = {
        id,
        nome,
        valor
      };
      alterarProduto(produtoEditado);
    }
  };

  return (
    <>
      <h1 style={{ textAlign: "center" }} tabIndex={1}>
        Editar Produto
      </h1>

      <div className="box">
        <p>Editando produto ID: {id}</p>
        <input
          type="text"
          placeholder="Nome do produto"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <input
          type="number"
          placeholder="Valor"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
        />
        <div className="grid-botoes">
          <button className="botao-salvar" onClick={handleSalvar}>Salvar Alterações</button>
          <button className="botao-cancelar" onClick={() => navigate("/")}>Cancelar</button>
        </div>
      </div>
    </>
  );
};

export default TelaProdutoDetalhes;
