import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import TelaProduto from "./TelaProduto";
import TelaProdutoDetalhes from "./TelaProdutoDetalhes";
import TelaProdutoEditar from "./TelaProdutoEditar";

function App() {
  return (
    <Router>
      <Routes>
        {/* Rota para a página principal que lista os produtos */}
        <Route path="/" element={<TelaProduto />} />

        {/* Rota para a página de detalhes, que captura o ID do produto */}
        <Route path="/detalhes/:id" element={<TelaProdutoDetalhes />} />
        <Route path="/detalhes/:id" element={<TelaProdutoEditar />} />
      </Routes>
    </Router>
  );
}

export default App;
