import { useState } from "react";

const FormularioProduto = ({ onAdicionar }) => {

const [nome, setNome] = useState("");
const [valor, setValor] = useState("");
const [mensagem, setMensagem] = useState({ texto: "", cor: "" });

const handleAdicionar = () => {

if (!nome || !valor) { // ID pode ser opcional
setMensagem({ texto: "Preencha pelo menos nome e valor!", cor: "red" });
return;
}

onAdicionar(nome, valor);
setMensagem({ texto: "Produto adicionado com sucesso!", cor: "green" });

setNome("");
setValor("");
};

return (
    <>
      {mensagem.texto && (
        <div style={{ color: mensagem.cor }} role="alert">{mensagem.texto}</div>
      )}

      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>

        {/* Campo Nome */}
        <input
          type="text"
          role="group"
          aria-label="Nome do produto"
          placeholder="Nome do produto"
          value={nome}
          tabIndex={1}
          onChange={(e) => setNome(e.target.value)}
        />

        {/* Campo Valor */}
        <input
          type="number"
          role="group"
          aria-label="Valor do produto"
          placeholder="Valor"
          value={valor}
          tabIndex={1}
          onChange={(e) => setValor(e.target.value)}
        />

        {/* Bot√£o Adicionar */}
        <button
          tabIndex={1}
          onClick={handleAdicionar}
        >
          Adicionar Produto
        </button>

      </div>
    </>
  );
};

export default FormularioProduto;
