import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const useGerenciadorProdutos = () => {
  const [listaProdutos, setListaProdutos] = useState(() => {
    const produtosSalvos = localStorage.getItem("listaProdutos");
    return produtosSalvos ? JSON.parse(produtosSalvos) : [];
  });

  useEffect(() => {
    localStorage.setItem("listaProdutos", JSON.stringify(listaProdutos));
  }, [listaProdutos]);

  const adicionarProduto = (id, nome, valor) => {
    const novoProduto = {
      // Usando o ID do formulário, mas garantindo que seja único se estiver vazio
      id: id || `prod-${Math.random()}`, 
      nome: nome,
      valor: valor
    };

    setListaProdutos(produtosAtuais => [...produtosAtuais, novoProduto]);
  };

  const excluirProduto = (id) => {
    const novaLista = listaProdutos.filter((produto) => produto.id !== id);
    setListaProdutos(novaLista);
  };

  const navigate = useNavigate();

  const navegarParaDetalhes = (id) => {
    // Navega para a URL de detalhes do produto
    navigate(`/detalhes/${id}`);
  };

  const alterarProduto = (produtoEditado) => {
    const novaLista = listaProdutos.map((produto) =>
      produto.id === produtoEditado.id ? produtoEditado : produto
    );
    setListaProdutos(novaLista);
    navigate("/"); // Volta para a lista após alterar
  };

  return {
    listaProdutos,
    adicionarProduto,
    excluirProduto,
    navegarParaDetalhes,
    alterarProduto
  };
};

export default useGerenciadorProdutos;
